import React, { Component } from 'react';
import './App.css';
import RadarChart from './Components/RadarChart';

import TopNavbar from './Components/TopNavbar';
import BottomNavbar from './Components/BottomNavbar';


class App extends Component {
  constructor() {
    super();
    this.state = {
      pokemonList: [],
      pokemonSpeciesList: [],
      selectedIndex: 0
    }
    this.pressed = false;
  }

  getPokemonList = async (pokemons) => {
    let promises = [];

    pokemons.forEach(p => {
      promises.push(
        fetch("https://pokeapi.co/api/v2/pokemon/" + p.pokemon_species.name + "/")
          .then(p => p.json())
        );
    });

    const result = await Promise.all(promises);
    return result;
  }

  getPokemonSpeciesList = async (pokemons) => {
    let promises = [];

    pokemons.forEach(p => {
      promises.push(
        fetch(p.pokemon_species.url)
          .then(p => p.json())
        );
    });

    const result = await Promise.all(promises);
    return result;
  }

  componentDidMount() {
    document.addEventListener("keydown", this.handleKeyDown.bind(this));

    fetch("https://pokeapi.co/api/v2/pokedex/kanto/")
    .then(result => result.json())

    .then(pokedex => {
        let pokemons = pokedex.pokemon_entries;

        this.getPokemonList(pokemons)
          .then(a => {
            this.setState({
              pokemonList: a
            });
          });

        this.getPokemonSpeciesList(pokemons)
        .then(a => {
          this.setState({
            pokemonSpeciesList: a
          });
        });
    });
  }

  handleKeyDown = event => {
    if(this.pressed)
      return;

    let {selectedIndex} = this.state;

    if(event.keyCode === 40 && selectedIndex < this.state.pokemonList.length - 1) {
      this.setState({
        selectedIndex: selectedIndex + 1
      });
    }

    if(event.keyCode === 38 && selectedIndex > 0) {
      this.setState({
        selectedIndex: selectedIndex - 1
      });
    }

    this.pressed = true;
    setTimeout(() => this.pressed = false, 300);
  }

  getRightDescription = language => {
    let rightIndex = 1;
    let p = this.state.pokemonSpeciesList[this.state.selectedIndex];

    for(let i = 0; i < p.flavor_text_entries.length; i++) {
      if(p.flavor_text_entries[i].language.name.localeCompare(language) === 0)
        rightIndex = i;
    } 

    return p.flavor_text_entries[rightIndex].flavor_text;
  }

  render() {
    if(this.state.pokemonList.length <= 0)
      return (<p>BOYYY</p>);

    const types = this.state.pokemonList[this.state.selectedIndex].types.map((item, index) => {
      return(
        <div className={"pure-u-1-2 type " + item.type.name} key={index}>
          <p className="type">{item.type.name}</p>
        </div>
      )
    });

    let rightDescription = this.getRightDescription("en");

    return (
      <div className="container">

        <TopNavbar />

        <div className="pure-g divider">
          <div className="pure-u-1-2" />
          <div className="pure-u-1-2 center-element">
            <button className="triangle-up">UP</button>
          </div>
        </div>

        <div className="pure-g body">

          <div className="pure-u-1-2 center-element">
            <img src={this.state.pokemonList[this.state.selectedIndex].sprites.front_default} className="pokemon-image"/>
          </div>

          <div className="pure-u-1-2">
            <h1 className="info-container">
              {"#" + (this.state.selectedIndex + 1) + " " + this.state.pokemonList[this.state.selectedIndex].name.toUpperCase()}
            </h1>

            <h1>{this.state.pokemonSpeciesList[this.state.selectedIndex].shape.name.charAt(0).toUpperCase() + this.state.pokemonSpeciesList[this.state.selectedIndex].shape.name.slice(1) + " pokemon"}</h1>

            <div>{types}</div>

            <div className="description">
              <p className="description">{rightDescription}</p>
            </div>

          </div>
        </div>

        <div className="pure-g divider">
          <div className="pure-u-1-2" />
          <div className="pure-u-1-2 center-element">
            <button className="triangle-down">DOWN</button>
          </div>
        </div>

        <BottomNavbar />

      </div>
    )
  }
}

export default App;
