import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      pokemonList: []
    }
  }

  componentDidMount() {
    fetch("https://pokeapi.co/api/v2/pokedex/kanto/")
    .then(result => result.json())

    .then(pokedex => {
        let list = [{"name": "AAA"}];
        let pokemons = pokedex.pokemon_entries;

        pokemons.forEach(pokemon => {
            fetch(pokemon.pokemon_species.url)
              .then(res => res.json())
              .then(pokemon => {
                list.push(pokemon);
                console.log(list.length);
                this.setState({
                  pokemonList: list
                })
              });
        });
    });
  }

  render() {
    //console.log(this.props.pokemons);
    return (
      <div className="App">
        {this.state.pokemonList[0].name}
      </div>
    );
  }
}

export default App;
