import React, { Component } from 'react';
import './App.css';
import Item from './Components/Item'

class App extends Component {
  constructor() {
    super();
    this.state = {
      pokemonList: [],
      pokemonSpeciesList: [],
      pokemonDescriptions: [],
      pokemonShapes: [],
      selectedIndex: 0
    }
    this.pressed = false;
  }

  getPokemonList = async (pokemons) => {
    let promises = [];

    pokemons.forEach(p => {
      promises.push(
        fetch("https://pokeapi.co/api/v2/pokemon/" + p.pokemon_species.name + "/")
          .then(p => p.json())
        );
    });

    const result = await Promise.all(promises);
    return result;
  }

  getPokemonSpeciesList = async (pokemons) => {
    let promises = [];

    pokemons.forEach(p => {
      promises.push(
        fetch(p.pokemon_species.url)
          .then(p => p.json())
        );
    });

    const result = await Promise.all(promises);
    return result;
  }

  getPokemonDescriptions = async (pokemons) => {
    let promises = [];

    pokemons.forEach(p => {
      promises.push(
        fetch(p.pokemon_species.url)
          .then(p => p.json())
          .then(p => {
            let rightIndex = 1;
            for(let i = 0; i < p.flavor_text_entries.length; i++) {
              if(p.flavor_text_entries[i].language.name.localeCompare("en") === 0)
                rightIndex = i;
            }

            return p.flavor_text_entries[rightIndex].flavor_text;
          })
        );
    });

    const result = await Promise.all(promises);
    return result;
  }

  getPokemonShapes = async (pokemons) => {
    let promises = [];

    pokemons.forEach(p => {
      promises.push(
        fetch(p.pokemon_species.url)
          .then(p => p.json())
          .then(p => p.shape.name)
        );
    });

    const result = await Promise.all(promises);
    return result;
  }

  componentDidMount() {
    document.addEventListener("keydown", this.handleKeyDown.bind(this));

    fetch("https://pokeapi.co/api/v2/pokedex/kanto/")
    .then(result => result.json())

    .then(pokedex => {
        let pokemons = pokedex.pokemon_entries;

        this.getPokemonList(pokemons)
          .then(a => {
            this.setState({
              pokemonList: a
            });
          });

        this.getPokemonSpeciesList(pokemons)
        .then(a => {
          this.setState({
            pokemonSpeciesList: a
          });
        });

        this.getPokemonDescriptions(pokemons)
          .then(a => {
            this.setState({
              pokemonDescriptions: a
            });
          });

        this.getPokemonShapes(pokemons)
          .then(a => {
            this.setState({
              pokemonShapes: a
            });
          });
    });
  }

  handleKeyDown = event => {
    if(this.pressed)
      return;

    let {selectedIndex} = this.state;

    if(event.keyCode === 40 && selectedIndex < this.state.pokemonList.length - 1) {
      this.setState({
        selectedIndex: selectedIndex + 1
      });
    }

    if(event.keyCode === 38 && selectedIndex > 0) {
      this.setState({
        selectedIndex: selectedIndex - 1
      });
    }

    this.pressed = true;
    setTimeout(() => this.pressed = false, 300);
  }

  render() {
    if(this.state.pokemonList.length <= 0)
      return (<p>BOYYY</p>);

    //this.state.pokemonList.forEach(p => console.log(p));

    /*
    let nuovoArr = this.state.pokemonList.map((item, index) => {
      return(
        <Item name={item.name} id={item.id} key={index} />
      )
    });

    return nuovoArr;


    let types = undefined;

    if(this.state.pokemonList[this.state.selectedIndex].types.length === 2) {
      types = 
        <div className="pure-g">
          <div className={"pure-u-1-2 type " + this.state.pokemonList[this.state.selectedIndex].types[0].type.name}>
            <p className="prova">{this.state.pokemonList[this.state.selectedIndex].types[0].type.name}</p>
          </div>
          <div className = {"pure-u-1-2 type " + this.state.pokemonList[this.state.selectedIndex].types[1].type.name}>
            <p className="prova">{this.state.pokemonList[this.state.selectedIndex].types[1].type.name}</p>
          </div>
        </div>
    }
    else {
      types = 
        <div className="pure-g">
          <div className={"pure-u-1-2 type " + this.state.pokemonList[this.state.selectedIndex].types[0].type.name}>
            <p className="prova">{this.state.pokemonList[this.state.selectedIndex].types[0].type.name}</p>
          </div>
        </div>
    }
    */

    const types = this.state.pokemonList[this.state.selectedIndex].types.map((item, index) => {
      return(
        <div className={"pure-u-1-2 type " + item.type.name} key={index}>
          <p className="type">{item.type.name}</p>
        </div>
      )
    })

    return (
      <div className="container">
        <div className="pure-g navbar-top">
          <div className="pure-u-1-3">
          </div>
        </div>

        <div className="pure-g body">
          <div className="pure-u-1-2 center-element">
            <img src={this.state.pokemonList[this.state.selectedIndex].sprites.front_default} className="pokemon-image"/>
          </div>
          <div className="pure-u-1-2">
            <h1 className="info-container">
              {"#" + (this.state.selectedIndex + 1) + " " + this.state.pokemonList[this.state.selectedIndex].name.toUpperCase()}
            </h1>

            <h1>{this.state.pokemonSpeciesList[this.state.selectedIndex].shape.name}</h1>

            <div>{types}</div>

            <div className="description">
              <p className="description">{this.state.pokemonDescriptions[this.state.selectedIndex]}</p>
            </div>

          </div>
        </div>

        <div className="pure-g navbar-bottom">
          <div className="pure-u-1-3">
          </div>
        </div>



      </div>
    )
  }
}

export default App;
