import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      pokemonList: []
    }
  }

  getPokemonList = async (pokemons) => {
    let promises = [];

    pokemons.forEach(p => {
      promises.push(
        fetch(p.pokemon_species.url)
          .then(p => p.json())
        );
    });

    const result = await Promise.all(promises);
    return result;
  }

  componentDidMount() {
    fetch("https://pokeapi.co/api/v2/pokedex/kanto/")
    .then(result => result.json())

    .then(pokedex => {
        let pokemons = pokedex.pokemon_entries;
        this.getPokemonList(pokemons)
          .then(a => {
            this.setState({
              pokemonList: a
            })
          })
    });
  }

  render() {
    if(this.state.pokemonList == [])
      return;

    let list = Array(...this.state.pokemonList)
    console.log(typeof list)
    list.forEach(p => console.log(p.name))

    return (
      <div>AAA</div>
    )
    //console.log(this.props.pokemons);
    /*return (
      <div className="App">
        ESKERE
      </div>
    );*/
  }
}

export default App;
